mapper.update:
UPDATE 
	pd_user_profile
SET
	modify_time = NOW()
	, username = #{username} 
	, name = #{name} 
	, nickname = #{nickname} 
	, gender = #{gender} 
	, location = #{location} 
	, signature = #{signature} 
	, email = #{email} 
	, wechat = #{wechat} 
	, sina_weibo = #{sinaWeibo} 
	, twiter = #{twiter} 
	, facebook = #{facebook} 
	, linkedin = #{linkedin} 
	, head_portrait = #{headPortrait} 
	, country = #{country} 
	, birthday = #{birthday} 
WHERE
	id = #{id}
